from typing import List, Dict, Any
from pydantic import BaseModel, Field
import pandas as pd

from langflow.base.langchain_utilities.model import LCToolComponent
from langflow.io import HandleInput, MessageTextInput, IntInput, Output
from langflow.schema import Data
from langflow.field_typing import Tool
from langchain.tools import StructuredTool

class VariableMatcherToolComponent(LCToolComponent):
    display_name = "CMIP6 Variable Matcher"
    description = "Filters CMIP6 variables based on exact variable name matches."
    icon = "match"
    name = "variable_matcher"
    
    inputs = [
        HandleInput(
            name="variable_info",
            display_name="Variable Info",
            input_types=["Data"],
            info="Processed CMIP6 variable information from the previous component.",
        ),
        MessageTextInput(
            name="variable_name",
            display_name="Variable Name",
            info="The exact variable name to filter for (e.g., 'tas').",
        ),
    ]
    
    outputs = [
        Output(display_name="Matched Variables", name="matched_variables", method="match_variables"),
        Output(display_name="Tool", name="tool", method="build_tool"),
    ]

    class VariableMatcherSchema(BaseModel):
        variable_name: str = Field(
            ..., 
            description="The exact variable name to filter for (e.g., 'tas')"
        )

    def _process_matches(self, variable_name: str) -> List[Dict[str, Any]]:
        """Internal method to filter variables by exact name match"""
        if not isinstance(self.variable_info, Data) or 'variable_info' not in self.variable_info.data:
            raise ValueError("Invalid variable info input. Expected Data object with 'variable_info' key.")
        
        varsdf = self.variable_info.data['variable_info']
        
        try:
            # Filter for exact matches
            filtered_df = varsdf[varsdf['variable'].str.lower() == variable_name.lower()]
            
            if filtered_df.empty:
                return [{"message": f"No matches found for variable '{variable_name}'"}]
            
            # Convert results to list of dictionaries
            results = [
                {
                    'variable': row['variable'],
                    'long_name': row['long_name'],
                    'comment': row['comment'],
                }
                for _, row in filtered_df.iterrows()
            ]
            
            return results

        except Exception as e:
            return [{"error": f"Error filtering variables: {str(e)}"}]

    def match_variables(self) -> List[Data]:
        """Method for component output"""
        try:
            results = self._process_matches(self.variable_name)
            data_list = [Data(data=result) for result in results]
            self.status = data_list
            return data_list
            
        except Exception as e:
            error_message = f"Error matching variables: {str(e)}"
            self.status = error_message
            return [Data(data={"error": error_message})]

    def build_tool(self) -> Tool:
        """Build and return the tool"""
        return StructuredTool.from_function(
            name="variable_matcher",
            description="Filter CMIP6 variables by exact variable name match. "
                      "Input should be the exact variable name (e.g., 'tas').",
            func=self._tool_function,
            args_schema=self.VariableMatcherSchema,
        )

    def _tool_function(self, variable_name: str) -> List[Dict[str, Any]]:
        """Function to be called by the tool"""
        try:
            return self._process_matches(variable_name)
        except Exception as e:
            return [{"error": f"Error matching variables: {str(e)}"}]

from langflow.custom import Component
from langflow.io import HandleInput, MessageTextInput, Output
from langflow.schema import Data

class YearRangeFilterComponent(Component):
    display_name = "Year Range Filter"
    description = "Filters CMIP6 datasets based on a specified year range."
    icon = "calendar"

    inputs = [
        HandleInput(
            name="dataset_info",
            display_name="Dataset Info",
            input_types=["Data"],
            info="Processed CMIP6 dataset information from the previous filter.",
        ),
        MessageTextInput(
            name="year_range",
            display_name="Year Range",
            info="The year range for filtering (format: START-END, e.g., '1980-2014' or '1000-NA' or 'NA-2100').",
        ),
    ]

    outputs = [
        Output(display_name="Filtered Datasets", name="filtered_datasets", method="filter_datasets"),
    ]

    def parse_year_range(self, year_range: str):
        start, end = year_range.split('-')
        start = int(start) if start.lower() != 'na' else None
        end = int(end) if end.lower() != 'na' else None
        return start, end

    def filter_datasets(self) -> Data:
        if not isinstance(self.dataset_info, Data) or 'filtered_df' not in self.dataset_info.data:
            raise ValueError("Invalid dataset info input. Expected Data object with 'filtered_df' key.")

        df = self.dataset_info.data['filtered_df']

        if 'start year' not in df.columns or 'end year' not in df.columns:
            raise ValueError("Expected 'start year' and 'end year' columns in dataset info DataFrame.")

        try:
            start_year, end_year = self.parse_year_range(self.year_range.strip())
            
            if start_year is not None:
                df = df[df['end year'] >= start_year]
            if end_year is not None:
                df = df[df['start year'] <= end_year]
            
            year_range_str = f"{start_year if start_year else 'NA'}-{end_year if end_year else 'NA'}"
            self.status = f"Filtered datasets within the year range {year_range_str}. {len(df)} datasets remain."
            return Data(data={"filtered_df": df})

        except ValueError:
            error_message = "Invalid year range input. Please enter a valid range (e.g., '1980-2014' or '1000-NA' or 'NA-2100')."
            self.status = error_message
            return Data(data={"error": error_message})
        except Exception as e:
            error_message = f"Error filtering datasets: {str(e)}"
            self.status = error_message
            return Data(data={"error": error_message})

from langflow.custom import Component
from langflow.io import HandleInput, IntInput, Output
from langflow.schema import Data

class URLExtractorComponent(Component):
    display_name = "URL Extractor"
    description = "Extracts URLs from filtered CMIP6 datasets."
    icon = "link"

    inputs = [
        HandleInput(
            name="dataset_info",
            display_name="Filtered Dataset Info",
            input_types=["Data"],
            info="Filtered CMIP6 dataset information from the previous filters.",
        ),
        IntInput(
            name="max_urls",
            display_name="Maximum URLs",
            info="Maximum number of URLs to extract (0 for all).",
            value=5,
        ),
    ]

    outputs = [
        Output(display_name="Extracted URLs", name="extracted_urls", method="extract_urls"),
    ]

    def extract_urls(self) -> Data:
        if not isinstance(self.dataset_info, Data) or 'filtered_df' not in self.dataset_info.data:
            raise ValueError("Invalid dataset info input. Expected Data object with 'filtered_df' key.")

        df = self.dataset_info.data['filtered_df']

        if 'URL' not in df.columns:
            raise ValueError("Expected 'URL' column in dataset info DataFrame.")

        try:
            if self.max_urls > 0:
                urls = df['URL'].head(self.max_urls).tolist()
            else:
                urls = df['URL'].tolist()

            result = []
            for i, url in enumerate(urls, 1):
                result.append(Data(data={
                    "url": url,
                    "index": i,
                    "filename": url.split('/')[-1]
                }))

            self.status = result
            return result

        except Exception as e:
            error_message = f"Error extracting URLs: {str(e)}"
            self.status = error_message
            return [Data(data={"error": error_message})]

