from langflow.custom import Component
from langflow.io import HandleInput, MessageTextInput, Output
from langflow.schema import Data

class MIPFilterComponent(Component):
    display_name = "MIP Filter"
    description = "Filters CMIP6 datasets based on the predicted MIP."
    icon = "filter"

    inputs = [
        HandleInput(
            name="dataset_info",
            display_name="Dataset Info",
            input_types=["Data"],
            info="Processed CMIP6 dataset information from the previous component.",
        ),
        MessageTextInput(
            name="mip_value",
            display_name="MIP Value",
            info="The MIP value predicted by the LLM.",
        ),
    ]

    outputs = [
        Output(display_name="Filtered Datasets", name="filtered_datasets", method="filter_datasets_by_mip"),
    ]

    def filter_datasets_by_mip(self) -> Data:
        if not isinstance(self.dataset_info, Data) or 'df' not in self.dataset_info.data:
            raise ValueError("Invalid dataset info input. Expected Data object with 'df' key.")

        df = self.dataset_info.data['df']

        if 'MIP' not in df.columns:
            raise ValueError("Expected 'MIP' column in dataset info DataFrame.")

        try:
            mip_value = self.mip_value.strip()
            if mip_value.lower() == 'none':
                # No filtering; pass all datasets
                filtered_df = df
                self.status = "No MIP filtering applied."
            else:
                # Filter the dataframe
                filtered_df = df[df['MIP'] == mip_value]

                if filtered_df.empty:
                    self.status = f"No datasets found for MIP '{mip_value}'."
                else:
                    self.status = f"Filtered datasets by MIP '{mip_value}'. Remaining datasets: {len(filtered_df)}"

            return Data(data={"filtered_df": filtered_df})

        except Exception as e:
            error_message = f"Error filtering datasets by MIP: {str(e)}"
            self.status = error_message
            return Data(data={"error": error_message})

